schema:
{
    "$schema": "http://json-schema.org/schema#",
    "$id": "https://disc.gsfc.nasa.gov/schemas/model.json",
    "type": "object",
    "required": [
        "title",
        "expiration",
        "start",
        "severity",
        "body"
    ],
    "properties": {
        "title": {
            "type": "string",
            "description": "A title that states the main subject matter of the alert focusing on key words and relevant dates."
        },
        "expiration": {
            "type": "string",
            "format": "date-time",
            "description": "Expiration date and time in which the alert should no longer be a major issue and brought to the users’ attention."
        },
        "start": {
            "type": "string",
            "format": "date-time",
            "description": "Start date and time that the alert should be available visually on the website."
        },
        "severity": {
            "type": "string",
            "default": "normal",
            "enum": ["normal", "emergency"],
            "description": "When an alert has a severity selected as “emergency”, it will display as a banner across the top of the website. This should only be used in cases when every GES DISC user should be aware of an issue. All other alerts should have the “normal” severity selected."
        },
        "body": {
            "type": "string",
            "contentMediaType": "text/html",
            "description": "Main content of the message."
        },
        "tags": {
            "type": "array",
            "title": "Tags",
            "items": {
                "type": "string",
                "enum": ["placeholder"]
            },
            "description": "Relevant search keywords for this content, including missions or prior portals.",
            "uniqueItems": true
        },
        "datasets": {
            "type": "array",
            "title": "Related Datasets",
            "items": {
                "type": "string",
                "enum": ["placeholder"]
            },
            "description": "List datasets relevant to the content that will automatically link to the dataset landing page.",
            "uniqueItems": true
        },
        "notes": {
            "type": "string",
            "title": "Author Comments",
            "description": "This comment field is designated for the author to describe changes made. Reviewer comments should be written in the comment tool, by navigating to the first icon above the document title.  The comment panel will appear on the right side of the screen." 
        }
    }
}
