schema:
{
    "$schema": "http://json-schema.org/schema#",
    "$id": "https://disc.gsfc.nasa.gov/schemas/model.json",
    "properties": {
        "Combined_EntryID": {
            "title": "Combined Entry ID for CMR",
            "description": "Combine the Short_Name and Version fields (ex. AIRH2CCF_006)",
            "type": "string"
        },
        "Entry_ID": {
            "title": "Entry ID",
            "type": "object",
            "properties": {
                "Short_Name": {
                    "title": "Short Name",
                    "type": "string",
                    "maxLength": 80
                },
                "Version": {
                    "type": "string",
                    "maxLength": 80
                }
            },
            "required": [
                "Short_Name",
                "Version"
            ]
        },
        "Entry_Title": {
            "title": "Entry Title",
            "type": "string",
            "maxLength": 1030
        },
        "Science_Keywords": {
            "title": "Science Keywords",
            "type": "array",
            "minItems": 1,
            "uniqueItems": true,
            "items": {
                "type": "string"
            }
        },
        "ISO_Topic_Category": {
            "title": "ISO Topic Category",
            "type": "array",
            "minItems": 1,
            "uniqueItems": true,
            "items": {
                "type": "string",
                "enum": [
                    "BIOTA",
                    "BOUNDARIES",
                    "CLIMATOLOGY/METEOROLOGY/ATMOSPHERE",
                    "DISASTER",
                    "ECONOMY",
                    "ELEVATION",
                    "ENVIRONMENT",
                    "EXTRA TERRESTRIAL",
                    "FARMING",
                    "GEOSCIENTIFIC INFORMATION",
                    "HEALTH",
                    "IMAGERY/BASE MAPS/EARTH COVER",
                    "INLAND WATERS",
                    "INTELLIGENCE/MILITARY",
                    "LOCATION",
                    "OCEANS",
                    "PLANNING CADASTRE",
                    "SOCIETY",
                    "STRUCTURE",
                    "TRANSPORTATION",
                    "UTILITIES/COMMUNICATIONS"
                ]
            }
        },
        "Organization": {
            "type": "array",
            "minItems": 1,
            "items": {
                "type": "object",
                "properties": {
                    "Organization_Type": {
                        "title": "Organization Type",
                        "type": "string",
                        "default": "ARCHIVER",
                        "enum": [
                            "ARCHIVER",
                            "DISTRIBUTOR",
                            "ORIGINATOR",
                            "PROCESSOR"
                        ]
                    },
                    "Organization_Name": {
                        "title": "Organization Name",
                        "type": "object",
                        "properties": {
                            "Short_Name": {
                                "title": "Short Name",
                                "type": "string",
                                "default": "NASA/GSFC/SED/ESD/GCDC/GESDISC"
                            },
                            "Long_Name": {
                                "title": "Long Name",
                                "type": "string",
                                "default": "Goddard Earth Sciences Data and Information Services Center (formerly Goddard DAAC), Global Change Data Center, Earth Sciences Divis ion, Science and Exploration Directorate, Goddard Space Flight Center, NASA"
                            }
                        },
                        "required": [
                            "Short_Name",
                            "Long_Name"
                        ]
                    },
                    "Organization_URL": {
                        "title": "Organization URL",
                        "type": "string",
                        "maxLength": 1024,
                        "format": "uri-reference",
                        "default": "https://disc.gsfc.nasa.gov"
                    },
                    "Personnel": {
                        "type": "array",
                        "minItems": 1,
                        "items": {
                            "type": "object",
                            "properties": {
                                "Role": {
                                    "type": "string",
                                    "default": "DATA CENTER CONTACT",
                                    "enum": [
                                        "DATA CENTER CONTACT"
                                    ]
                                },
                                "Contact_Group": {
                                    "title": "Contact Group",
                                    "type": "array",
                                    "items": {
                                        "type": "object",
                                        "properties": {
                                            "Name": {
                                                "type": "string",
                                                "maxLength": 255,
                                                "default": "GES DISC HELP DESK SUPPORT GROUP"
                                            },
                                            "Address": {
                                                "type": "object",
                                                "properties": {
                                                    "Street_Address": {
                                                        "title": "Street Address",
                                                        "type": "array",
                                                        "items": {
                                                            "type": "string",
                                                            "maxLength": 1024
                                                        }
                                                    },
                                                    "City": {
                                                        "type": "string",
                                                        "maxLength": 100,
                                                        "default": "Greenbelt"
                                                    },
                                                    "State_Province": {
                                                        "title": "State/Province",
                                                        "type": "string",
                                                        "maxLength": 100,
                                                        "default": "MD"
                                                    },
                                                    "Postal_Code": {
                                                        "title": "Postal Code",
                                                        "type": "string",
                                                        "maxLength": 20,
                                                        "default": "20771"
                                                    },
                                                    "Country": {
                                                        "type": "string",
                                                        "maxLength": 100,
                                                        "default": "USA"
                                                    }
                                                }
                                            },
                                            "Phone": {
                                                "type": "array",
                                                "items": {
                                                    "type": "object",
                                                    "properties": {
                                                        "Number": {
                                                            "type": "string",
                                                            "maxLength": 1024
                                                        },
                                                        "Type": {
                                                            "type": "string",
                                                            "enum": [
                                                                "Telephone",
                                                                "Fax"
                                                            ]
                                                        }
                                                    }
                                                }
                                            },
                                            "Email": {
                                                "type": "array",
                                                "items": {
                                                    "type": "string",
                                                    "maxLength": 1024,
                                                    "default": "gsfc-dl-help-disc@mail.nasa.gov"
                                                }
                                            }
                                        },
                                        "required": [
                                            "Name"
                                        ]
                                    }
                                },
                                "Contact_Person": {
                                    "title": "Contact Person",
                                    "type": "array",
                                    "items": {
                                        "type": "object",
                                        "properties": {
                                            "First_Name": {
                                                "title": "First Name",
                                                "type": "string",
                                                "maxLength": 255
                                            },
                                            "Middle_Name": {
                                                "title": "Middle Name",
                                                "type": "string",
                                                "maxLength": 255
                                            },
                                            "Last_Name": {
                                                "title": "Last Name",
                                                "type": "string",
                                                "maxLength": 255
                                            },
                                            "Address": {
                                                "type": "object",
                                                "properties": {
                                                    "Street_Address": {
                                                        "title": "Street Address",
                                                        "type": "array",
                                                        "items": {
                                                            "type": "string",
                                                            "maxLength": 1024
                                                        }
                                                    },
                                                    "City": {
                                                        "type": "string",
                                                        "maxLength": 100
                                                    },
                                                    "State_Province": {
                                                        "title": "State/Province",
                                                        "type": "string",
                                                        "maxLength": 100
                                                    },
                                                    "Postal_Code": {
                                                        "title": "Postal Code",
                                                        "type": "string",
                                                        "maxLength": 20
                                                    },
                                                    "Country": {
                                                        "type": "string",
                                                        "maxLength": 100
                                                    }
                                                }
                                            },
                                            "Phone": {
                                                "type": "array",
                                                "items": {
                                                    "type": "object",
                                                    "properties": {
                                                        "Number": {
                                                            "type": "string",
                                                            "maxLength": 1024
                                                        },
                                                        "Type": {
                                                            "type": "string",
                                                            "enum": [
                                                                "Telephone",
                                                                "Fax"
                                                            ]
                                                        }
                                                    }
                                                }
                                            },
                                            "Email": {
                                                "type": "array",
                                                "items": {
                                                    "type": "string",
                                                    "maxLength": 1024
                                                }
                                            }
                                        },
                                        "required": [
                                            "Last_Name"
                                        ]
                                    }
                                }
                            }
                        }
                    },
                    "Dataset_ID": {
                        "title": "Dataset ID",
                        "type": "array",
                        "items": {
                            "type": "string",
                            "maxLength": 160
                        }
                    }
                },
                "required": [
                    "Organization_Type",
                    "Organization_Name",
                    "Organization_URL",
                    "Personnel"
                ]
            }
        },
        "Summary": {
            "type": "object",
            "properties": {
                "Abstract": {
                    "type": "string",
                    "pattern": "^((?!(http|https|ftp|www\\.))[\\s\\S])*$",
                    "maxLength": 12000
                },
                "Purpose": {
                    "type": "string",
                    "pattern": "^((?!\\.org|\\.com|\\.gov|http|https|ftp|www\\.)[\\s\\S])*$",
                    "maxLength": 10000
                }
            },
            "required": [
                "Abstract"
            ]
        },
        "Related_URL": {
            "title": "Related URL",
            "type": "array",
            "minItems": 1,
            "items": {
                "type": "object",
                "properties": {
                    "URL_Content_Type": {
                        "title": "URL Content Type",
                        "type": "object",
                        "properties": {
                            "Type_Subtype": {
                                "title": "Related URL Type and Subtype",
                                "type": "string",
                                "enum": [
                                    "placeholder"
                                ]
                            }
                        },
                        "required": [
                            "Type_Subtype"
                        ]
                    },
                    "URL": {
                        "type": "string",
                        "pattern": "^((?!\\.sci\\.)[\\s\\S])*$",
                        "maxLength": 1024,
                        "format": "uri-reference"
                    },
                    "Title": {
                        "type": "string"
                    },
                    "Description": {
                        "type": "string",
                        "maxLength": 12000
                    }
                },
                "required": [
                    "URL_Content_Type",
                    "URL"
                ]
            }
        },
        "Platform": {
            "type": "array",
            "minItems": 1,
            "uniqueItems": true,
            "items": {
                "type": "object",
                "properties": {
                    "Name": {
                        "title": "Platform Type, Short Name, and Long Name",
                        "type": "string",
                        "enum": [
                            "placeholder"
                        ]
                    },
                    "Characteristics": {
                        "type": "array",
                        "minItems": 0,
                        "items": {
                            "type": "object",
                            "properties": {
                                "Name": {
                                    "type": "string",
                                    "maxLength": 80
                                },
                                "Description": {
                                    "type": "string",
                                    "maxLength": 2048
                                },
                                "DataType": {
                                    "title": "Data Type",
                                    "type": "string",
                                    "maxLength": 80,
                                    "enum": [
                                        "STRING",
                                        "FLOAT",
                                        "INT",
                                        "BOOLEAN",
                                        "DATE",
                                        "TIME",
                                        "DATETIME",
                                        "DATE_STRING",
                                        "TIME_STRING",
                                        "DATETIME_STRING"
                                    ]
                                },
                                "Unit": {
                                    "type": "string",
                                    "maxLength": 20
                                },
                                "Value": {
                                    "type": "string",
                                    "maxLength": 80
                                }
                            },
                            "required": [
                                "Name",
                                "Description",
                                "DataType",
                                "Unit",
                                "Value"
                            ]
                        }
                    },
                    "Instrument": {
                        "type": "array",
                        "minItems": 1,
                        "items": {
                            "type": "object",
                            "properties": {
                                "Name": {
                                    "title": "Instrument Short and Long Name",
                                    "type": "string",
                                    "enum": [
                                        "placeholder"
                                    ]
                                },
                                "Sensor": {
                                    "type": "array",
                                    "minItems": 0,
                                    "items": {
                                        "type": "object",
                                        "properties": {
                                            "Short_Name": {
                                                "type": "string"
                                            }
                                        }
                                    }
                                }
                            },
                            "required": [
                                "Name"
                            ]
                        }
                    }
                },
                "required": [
                    "Name",
                    "Instrument"
                ]
            }
        },
        "Temporal_Coverage": {
            "title": "Temporal Coverage",
            "type": "array",
            "minItems": 1,
            "items": {
                "type": "object",
                "properties": {
                    "Range_DateTime": {
                        "title": "DateTime Range",
                        "type": "array",
                        "items": {
                            "type": "object",
                            "properties": {
                                "Beginning_Date_Time": {
                                    "title": "Beginning Date Time",
                                    "type": "string",
                                    "pattern": "^[0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?Z?$"
                                },
                                "Ending_Date_Time": {
                                    "title": "Ending Date Time",
                                    "type": "string",
                                    "pattern": "^[0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?Z?$"
                                }
                            },
                            "required": [
                                "Beginning_Date_Time"
                            ]
                        }
                    },
                    "Ends_At_Present_Flag": {
                        "title": "Ends At Present Flag",
                        "type": "string",
                        "enum": [
                            "true",
                            "false"
                        ]
                    }
                }
            }
        },
        "Spatial_Coverage": {
            "title": "Spatial Coverage",
            "type": "object",
            "properties": {
                "Spatial_Coverage_Type": {
                    "title": "Spatial Coverage Type",
                    "type": "string",
                    "enum": [
                        "Horizontal",
                        "HorizontalVertical",
                        "Orbit",
                        "Vertical",
                        "Horizon&Vert"
                    ]
                },
                "Granule_Spatial_Representation": {
                    "title": "Granule Spatial Representation",
                    "type": "string",
                    "enum": [
                        "CARTESIAN",
                        "GEODETIC",
                        "ORBIT",
                        "NO_SPATIAL"
                    ]
                },
                "Geometry": {
                    "type": "object",
                    "properties": {
                        "Coordinate_System": {
                            "title": "Coordinate System",
                            "type": "string",
                            "enum": [
                                "CARTESIAN",
                                "GEODETIC"
                            ]
                        },
                        "Bounding_Rectangle": {
                            "title": "Bounding Rectangle",
                            "type": "array",
                            "items": {
                                "type": "object",
                                "properties": {
                                    "Southernmost_Latitude": {
                                        "title": "Southernmost Latitude",
                                        "type": "number",
                                        "minimum": -90.0,
                                        "maximum": 90.0
                                    },
                                    "Northernmost_Latitude": {
                                        "title": "Northernmost Latitude",
                                        "type": "number",
                                        "minimum": -90.0,
                                        "maximum": 90.0
                                    },
                                    "Westernmost_Longitude": {
                                        "title": "Westernmost Longitude",
                                        "type": "number",
                                        "minimum": -180.0,
                                        "maximum": 180.0
                                    },
                                    "Easternmost_Longitude": {
                                        "title": "Easternmost Longitude",
                                        "type": "number",
                                        "minimum": -180.0,
                                        "maximum": 180.0
                                    },
                                    "Minimum_Altitude": {
                                        "title": "Minimum Altitude",
                                        "type": "string"
                                    },
                                    "Maximum_Altitude": {
                                        "title": "Maximum Altitude",
                                        "type": "number"
                                    },
                                    "Altitude_Unit": {
                                        "title": "Altitude Unit",
                                        "type": "string"
                                    },
                                    "Minimum_Depth": {
                                        "title": "Minimum Depth",
                                        "type": "number"
                                    },
                                    "Maximum_Depth": {
                                        "title": "Maximum Depth",
                                        "type": "number"
                                    },
                                    "Depth_Unit": {
                                        "title": "Depth Unit",
                                        "type": "string"
                                    }
                                }
                            }
                        }
                    }
                },
                "Orbit_Parameters": {
                    "title": "Orbit Parameters",
                    "type": "array",
                    "minItems": 0,
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "properties": {
                            "Swath_Width": {
                                "title": "Swath Width",
                                "type": "number"
                            },
                            "Period": {
                                "type": "number"
                            },
                            "Inclination_Angle": {
                                "title": "Inclination Angle",
                                "type": "number"
                            },
                            "Number_Of_Orbits": {
                                "title": "Number Of Orbits",
                                "type": "number"
                            },
                            "Start_Circular_Latitude": {
                                "title": "Start Circular Latitude",
                                "type": "number",
                                "minimum": -90.0,
                                "maximum": 90.0
                            }
                        },
                        "required": [
                            "Swath_Width",
                            "Period",
                            "Inclination_Angle",
                            "Number_Of_Orbits"
                        ]
                    }
                },
                "Spatial_Info": {
                    "title": "Spatial Info",
                    "type": "array",
                    "minItems": 0,
                    "maxItems": 1,
                    "items": {
                        "type": "object",
                        "properties": {
                            "Spatial_Coverage_Type": {
                                "title": "Spatial Coverage Type",
                                "type": "string",
                                "enum": [
                                    "Horizontal",
                                    "HorizontalVertical",
                                    "Orbit",
                                    "Vertical",
                                    "Horizon&Vert"
                                ]
                            },
                            "Horizontal_Coordinate_System": {
                                "title": "Horizontal Coordinate System",
                                "type": "object",
                                "properties": {
                                    "Geodetic_Model": {
                                        "title": "Geodetic Model",
                                        "type": "object",
                                        "properties": {
                                            "Horizontal_DatumName": {
                                                "title": "Horizontal Datum Name",
                                                "type": "string",
                                                "maxLength": 80
                                            }
                                        }
                                    }
                                }
                            }
                        },
                        "required": [
                            "Spatial_Coverage_Type"
                        ]
                    }
                }
            },
            "required": [
                "Granule_Spatial_Representation"
            ],
            "dependencies": {
                "Granule_Spatial_Representation": {
                    "oneOf": [
                        {
                            "properties": {
                                "Granule_Spatial_Representation": {
                                    "enum": [
                                        "NO_SPATIAL"
                                    ]
                                }
                            }
                        },
                        {
                            "properties": {
                                "Granule_Spatial_Representation": {
                                    "enum": [
                                        "CARTESIAN",
                                        "GEODETIC",
                                        "ORBIT"
                                    ]
                                },
                                "Geometry": {
                                    "required": [
                                        "Coordinate_System"
                                    ],
                                    "properties": {
                                        "Bounding_Rectangle": {
                                            "required": [
                                                "Southernmost_Latitude",
                                                "Northernmost_Latitude",
                                                "Westernmost_Longitude",
                                                "Easternmost_Longitude"
                                            ]
                                        }
                                    }
                                }
                            }
                        }
                    ]
                }
            }
        },
        "Project": {
            "type": "array",
            "minItems": 1,
            "items": {
                "type": "object",
                "properties": {
                    "Name": {
                        "title": "Project Short and Long Name",
                        "type": "string",
                        "enum": [
                            "placeholder"
                        ]
                    },
                    "Campaign": {
                        "type": "string",
                        "maxLength": 80
                    }
                },
                "required": [
                    "Name"
                ]
            }
        },
        "Metadata_Dates": {
            "title": "Metadata Dates",
            "type": "object",
            "properties": {
                "Metadata_Creation": {
                    "title": "Metadata Creation",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                },
                "Metadata_Last_Revision": {
                    "title": "Metadata Last Revision",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                },
                "Data_Creation": {
                    "title": "Data Creation",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                },
                "Data_Last_Revision": {
                    "title": "Data Last Revision",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                },
                "Metadata_Future_Review": {
                    "title": "Metadata Future Review",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                },
                "Data_Future_Review": {
                    "title": "Data Future Review",
                    "type": "string",
                    "pattern": "^([0-9]{4}-[0-9]{2}-[0-9]{2}(T[0-9]{2}(:[0-9]{2}(:[0-9]{2}(\\.[0-9]+)?)?)?)?)|([Nn]ot provided)|([Uu]nknown)|([Pp]resent)|([Uu]nbounded)$"
                }
            },
            "required": [
                "Metadata_Creation",
                "Metadata_Last_Revision",
                "Data_Creation",
                "Data_Last_Revision"
            ]
        },
        "Dataset_Citation": {
            "title": "Dataset Citation",
            "type": "array",
            "items": {
                "type": "object",
                "properties": {
                    "Dataset_Creator": {
                        "title": "Dataset Creator",
                        "type": "string",
                        "maxLength": 1024
                    },
                    "Dataset_Editor": {
                        "title": "Dataset Editor",
                        "type": "string",
                        "maxLength": 1024
                    },
                    "Dataset_Title": {
                        "title": "Dataset Title",
                        "type": "string",
                        "maxLength": 1024
                    },
                    "Dataset_Series_Name": {
                        "title": "Dataset Series Name",
                        "type": "string",
                        "maxLength": 1024
                    },
                    "Dataset_Release_Date": {
                        "title": "Dataset Release Date",
                        "type": "string",
                        "pattern": "^[0-9]{4}-[0-9]{2}-[0-9]{2}$"
                    },
                    "Dataset_Release_Place": {
                        "title": "Dataset Release Place",
                        "type": "string",
                        "maxLength": 1024
                    },
                    "Dataset_Publisher": {
                        "title": "Dataset Publisher",
                        "type": "string",
                        "maxLength": 1024,
                        "default": "Goddard Earth Sciences Data and Information Services Center (GES DISC)"
                    },
                    "Version": {
                        "title": "Dataset Version",
                        "type": "string",
                        "maxLength": 80
                    },
                    "Issue_Identification": {
                        "title": "Issue Identification",
                        "type": "string",
                        "maxLength": 80
                    },
                    "Data_Presentation_Form": {
                        "title": "Data Presentation Form",
                        "type": "string",
                        "default": "Digital Science Data",
                        "maxLength": 80
                    },
                    "Other_Citation_Details": {
                        "title": "Other Citation Details",
                        "type": "string",
                        "maxLength": 4000
                    },
                    "Online_Resource": {
                        "title": "Online Resource",
                        "type": "string",
                        "maxLength": 600,
                        "format": "uri-reference",
                        "pattern": "^https:\/\/(?!.*\\.sci\\.).*$"
                    },
                    "Persistent_Identifier": {
                        "title": "Persistent Identifier",
                        "type": "object",
                        "properties": {
                            "Type": {
                                "type": "string",
                                "enum": [
                                    "DOI", 
